import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import { Button, Form, Input, message, Radio, DatePicker } from 'antd';
import './HireMeForm.css';
const key = 'updatable';
export default function ContactUs({
  citiesArray,
  Tguide_email = 'gtour180@gmail.com',
  Tguide_name = 'guide1',
  name,
}) {
  const [form] = Form.useForm();
  const [selecteditem, setSelecteditem] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  function sendEmail() {
    emailjs
      .sendForm(
        'service_ptqxdat',
        'template_3x6q9lo',
        '.hiremeform',
        'user_TjOtCbLf6rKNt6BePm7vw'
      )
      .then(
        (result) => {
          message.loading({ content: 'Loading...', key });
          setTimeout(() => {
            message.success({
              content: 'Message Sent Successfuly!',
              key,
              duration: 2,
            });
          }, 1000);
        },
        (error) => {
          message.loading({ content: 'Loading...', key });
          setTimeout(() => {
            message.error({
              content: 'Something went wrong, try again',
              key,
              duration: 2,
            });
          }, 1000);
        }
      );
    form.resetFields();
  }
  const handleSubmit = (values) => {
    sendEmail();
  };
  return (
    <div>
      <Form form={form} onFinish={handleSubmit} className="hiremeform ">
        <div className="hireMe-div">
          <div className="left-div-p">
            <h1 className="left-div-text">
              {' '}
              <img
                alt="left side background"
                className="left-div-img"
                src="data:image/svg+xml;base64,"
              />{' '}
              <br />
              Email The tour guider <br />
              <span>'{name}'</span>
            </h1>
          </div>
          <div className="form-div">
            <div className="calendar-div">
              <div className="email-name-mess-c">
                <Input type="hidden" name="Tguide_email" value={Tguide_email} />
                <Input type="hidden" name="Tguide_name" value={Tguide_name} />
                <Form.Item
                  label="Full Name"
                  name="user_name"
                  rules={[
                    {
                      required: true,
                      message: 'Please input your full name!',
                    },
                  ]}
                >
                  <Input type="text" name="user_name" className="input_name" />
                </Form.Item>
                <Form.Item
                  label="Email"
                  name="user_email"
                  rules={[
                    {
                      required: true,
                      message: 'Please input your email!',
                    },
                  ]}
                >
                  <Input
                    type="email"
                    name="user_email"
                    className="input_email"
                  />
                </Form.Item>
                <Form.Item
                  label="Message"
                  name="message"
                  rules={[
                    {
                      required: true,
                      message: 'Please input your message!',
                    },
                  ]}
                >
                  <Input.TextArea name="message" className="input_message" />
                </Form.Item>
                <Form.Item
                  rules={[
                    {
                      required: true,
                      message: 'Please input the date!',
                    },
                  ]}
                  label="Pick a date"
                >
                  <DatePicker
                    onChange={(value) => {
                      setSelectedDate(
                        value._d.toString().split(' ').slice(0, 4).join(' ')
                      );
                    }}
                    name="date"
                  />
                  <Input
                    type="hidden"
                    name="date"
                    className="date"
                    value={selectedDate}
                  />
                </Form.Item>
                <Form.Item name="City" label="City">
                  <div className="checkbox-headline">
                    <div className="checkbox-cont">
                      {citiesArray.map((item, index) => (
                        <Radio
                          name="city"
                          onChange={(v) => {
                            setSelecteditem(v.target.value);
                          }}
                          key={index}
                          value={item.name}
                          className="checkbox-single-value"
                        >
                          {item.name}
                        </Radio>
                      ))}
                    </div>
                  </div>
                </Form.Item>

                <div className="btn-submit-send">
                  <Form.Item>
                    <Button
                      className="send-btn"
                      type="primary"
                      htmlType="submit"
                    >
                      Send
                    </Button>
                  </Form.Item>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Form>
    </div>
  );
}
